function getWiki(){$.ajax({url:"http://en.wikipedia.org/w/api.php?action=query&generator=random&grnnamespace=0&prop=extracts|info&inprop=url&exchars=1000&format=json",data:{format:"json"},dataType:"jsonp",success:function(e){console.log(e);for(var a in e.query.pages){var r=e.query.pages[a].extract,t=r.split("</p>"),i='<a href="'+e.query.pages[a].fullurl+'">View Full Article: "'+e.query.pages[a].title+'"</a>';$("article").html(t[0]+"</p>"+i).addClass("on")}}})}$(document).ready(function(){getWiki(),$("#button").click(function(){$("article").removeClass("on"),getWiki()})});