function getWiki(){$.ajax({url:"http://en.wikipedia.org/w/api.php?action=query&generator=random&grnnamespace=0&prop=extracts|info&inprop=url&exchars=1000&format=json",data:{format:"json"},dataType:"jsonp",success:function(e){console.log(e);for(var a in e.query.pages){var t=e.query.pages[a].extract,r=t.split("</p>"),i='<a href="'+e.query.pages[a].fullurl+'">View Full Article: "'+e.query.pages[a].title+'"</a>';$("article").html(r[0]+"</p>"+i)}}})}$(document).ready(function(){getWiki(),$("#button").click(function(){getWiki()})});